import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
    name: 'colorStatusActive',
    standalone: true
})
export class ColorStatusActivePipe implements PipeTransform {

  transform({ active }: any, prefix: string = ''): any {
    const color = active === true ? 'success' : active === false ? 'warning' : 'default';
    return prefix + color;
  }

}

import { Pipe, PipeTransform } from '@angular/core';
import { parseISO, intlFormatDistance } from 'date-fns';
@Pipe({
  name: 'diffDate',
  standalone: true,
})
export class DiffDatePipe implements PipeTransform {
  transform(value: string, referenceDate: Date = new Date()): any {
    const date = parseISO(value);
    return intlFormatDistance(date, referenceDate);
  }
}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
    name: 'enableQuotation',
    standalone: true
})
export class EnableQuotationPipe implements PipeTransform {

    transform(enable: any, args: any[]): boolean {
        const [authUser, quotation] = args;
        if (authUser.id != quotation.seller_id && authUser.id != quotation.project_manager_id && quotation.last_quotation_workflow?.status == 'cotizando') {
            enable = true;
        }
        return true;
    }

}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'executeFunctionList',
  standalone: true
})
export class ExecuteFunctionListPipe implements PipeTransform {

  transform(data: any, [item, index]: [item: any, index: number]): any {
    if(data instanceof Function) return data(item, index);
    return data;
  }

}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'executeFunction',
  standalone: true
})
export class ExecuteFunctionPipe implements PipeTransform {

  transform(fn: Function, parameter: any): any {
    return fn(parameter);
  }

}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'firstLetterName',
  standalone: true,
})
export class FirstLetterNamePipe implements PipeTransform {

  transform(value: string = '', numberLetter: 1 | 2 = 2): unknown {
    let keysName = '';
    const array = value.split(' ');
    if(array.length > 1) {
      if (numberLetter > 0) keysName += array[0].substring(0, 1);
      if (numberLetter > 1) keysName += array[1].substring(0, 1);
    } else {
      if (numberLetter > 0) keysName += array[0].substring(0, 1);
      if (numberLetter > 1) keysName += array[0].substring(1, 2);
    }
    return keysName.toUpperCase();
  }

}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'firstLetterUppercase',
  standalone: true
})
export class FirstLetterUppercasePipe implements PipeTransform {

  transform(value: string | null): string | null {
    if(value) return value.charAt(0).toUpperCase() + value.slice(1);
    return value;
  }

}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'generatePathUrl',
  standalone: true
})
export class GeneratePathUrlPipe implements PipeTransform {

  transform(prefix: string, valueConcat: string | undefined | null | boolean): string {
    const path = this.cleanPath(prefix);
    return valueConcat ? `${path}/${valueConcat}` : path;
  }

  private cleanPath(path: string) {
    return path.endsWith('/') ? path.slice(0, -1) : path;
  }

}

