import { Pipe, PipeTransform, inject } from '@angular/core';
import { DatabaseStorageService, NameModuleDatabase } from '@service/database-storage.service';
import { Observable } from 'rxjs';

@Pipe({
  name: 'getItemFromLocalData',
  standalone: true
})
export class GetItemFromLocalDataPipe implements PipeTransform {

  private databaseStorage = inject(DatabaseStorageService);

  transform<T = any>(id: number | null, nameModuleDatabase: NameModuleDatabase): Observable<T | null | undefined> {
    return new Observable((subscriber) => {
      if(!id) {
        subscriber.next(null);
        subscriber.complete();
      } else {
        this.databaseStorage.getOne<T>(nameModuleDatabase, id).then(item => {
          subscriber.next(item);
          subscriber.complete();
        });
      }
    });
  }

}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
    name: 'getKeyItem',
    standalone: true,
})
export class GetKeyItemPipe implements PipeTransform {

    transform(item: { [key: string]: any }, key: string | false): any {
        if (!item) return '';
        if (!key) return '';
        const keys = key.split('.') ?? [];
        if (!keys.length) return '';
        try {
            if (keys.length == 6) return item[keys[0]][keys[1]][keys[2]][keys[3]][keys[4]][keys[5]] ?? ''
            if (keys.length == 5) return item[keys[0]][keys[1]][keys[2]][keys[3]][keys[4]] ?? ''
            if (keys.length == 4) return item[keys[0]][keys[1]][keys[2]][keys[3]] ?? ''
            if (keys.length == 3) return item[keys[0]][keys[1]][keys[2]] ?? ''
            if (keys.length == 2) return item[keys[0]][keys[1]] ?? ''
            if (keys.length == 1) return item[keys[0]] ?? ''
        } catch (error) {
            return '';
        }
        return '';
    }

}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'getMixedValue',
  standalone: true
})
export class GetMixedValuePipe implements PipeTransform {

  transform(value: any, parameter: any): any {
    if(typeof value === 'function') {
      return value(parameter);
    } else {
      return value;
    }
  }

}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'getNameMonth',
