import { Pipe, PipeTransform, inject } from '@angular/core';
import { User } from '@interface/user';
import { DatabaseStorageService, NameModuleDatabase } from '@service/database-storage.service';
import { Observable } from 'rxjs';

@Pipe({
  name: 'getUserById',
  standalone: true
})
export class GetUserByIdPipe implements PipeTransform {

  private databaseStorage = inject(DatabaseStorageService);

  async transform(id: number | null | undefined): Promise<User | null | undefined> {
    if(!id) return null;
    return await this.databaseStorage.getOne<User>(NameModuleDatabase.Users, id);
  }

}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'hiddenOptionButtonRow',
  standalone: true
})
export class HiddenOptionButtonRowPipe implements PipeTransform {

  transform(idOption: string | number, idsHidden: unknown[]): boolean {
    if(!idOption || !idsHidden || !(idsHidden instanceof Array) || !idsHidden.length) return false;
    return idsHidden.includes(idOption);
  }

}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
    name: 'imageMimeTypeFile',
    standalone: true,
})
export class ImageMimeTypeFilePipe implements PipeTransform {
    transform(nameFile: string | null): string {
        if (!nameFile) return '';
        const extension_file: string | undefined = nameFile.split('.').pop();
        if (!extension_file) return '';
        if (
            /(png|jpg|jpeg|webp)/.test(extension_file) &&
            /(http|https)/.test(nameFile)
        ) {
            return nameFile;
        } if (/(png|jpg|jpeg|webp)/.test(extension_file)) {
            return '/assets/svg-icons/image-file.svg';
        } else if (/(docx?)/.test(extension_file)) {
            return '/assets/svg-icons/microsoft-word.svg';
        } else if (/(xlsx?|csv)/.test(extension_file)) {
            return '/assets/svg-icons/microsoft-excel.svg';
        } else if (/(pptx?)/.test(extension_file)) {
            return '/assets/svg-icons/microsoft-powerpoint.svg';
        } else if (/(pdf)/.test(extension_file)) {
            return '/assets/svg-icons/pdf.png';
        } else if (/(txt)/.test(extension_file)) {
            return '/assets/svg-icons/document.svg';
        } else if (/(mpp)/.test(extension_file)) {
            return '/assets/svg-icons/microsoft-excel.svg';
        }
        return '';
    }
}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'indexList',
  standalone: true,
})
export class IndexListPipe implements PipeTransform {

  transform(index: number, [pageIndex,PageSize]: [number, number]): unknown {
    return pageIndex * PageSize + (index + 1);
  }

}

import { Pipe, PipeTransform } from '@angular/core';
interface ListFormatOptions {
  lang?: string;
  type?: 'conjunction' | 'disjunction' | 'unit';
  style?: 'long' | 'short' | 'narrow';
}

@Pipe({
  name: 'listFormat',
  standalone: true
})
export class ListFormatPipe implements PipeTransform {

  transform(values: string[], options?:ListFormatOptions): string {
    if(!(values instanceof Array)) return '';
    else if (values.length && (values[0] as any) instanceof Object) return '';
    const { lang = 'es-ES', type = 'conjunction', style = 'long' } = options ?? {};
    return (new Intl.ListFormat(lang, { style, type}).format(values)) ?? '';
  }

}

import { Pipe, PipeTransform } from '@angular/core';
import { User, UserlevelEnum } from '@interface/user';

@Pipe({
  name: 'nameLevelUser',
  standalone: true
})
export class NameLevelUserPipe implements PipeTransform {

  transform(user: User): string {
    const values = Object.entries(UserlevelEnum);
    const nameLevel = values.find(value => {
      return value[1] == user.level
    })
    return nameLevel ? nameLevel[0] : '';
  }

}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'nameMimeTypeFile',
  standalone: true,
})
export class NameMimeTypeFilePipe implements PipeTransform {

  transform(value: string | null | undefined, ...args: unknown[]): unknown {
    let name = '--';
    if(value) {
        const extension_file: string | undefined = value.split('.').pop();
        if (!extension_file) return name;
        if (/(docx?)/.test(extension_file)) {
          name = 'word';
        } else if (/(csv)/.test(extension_file)) {
          name = 'csv';
        } else if (/(xlsx?)/.test(extension_file)) {
          name = 'excel';
        } else if (/(pptx?)/.test(extension_file)) {
          name = 'powerpoint';
        } else if (/(png|jpg|jpeg|webp)/.test(extension_file)) {
          name = 'imagen';
        } else if (/(pdf)/.test(extension_file)) {
          name = 'pdf';
        } else if (/(txt)/.test(extension_file)) {
          name = 'text';
        } else if (/(mp4|avi)/.test(extension_file)) {
          name = 'video';
        } else if (/(mp3)/.test(extension_file)) {
          name = 'audio';
        } else if (/(mpp)/.test(extension_file)) {
          name = 'ms project';
        }
    }
    return name;
  }

}

import { Pipe, PipeTransform } from '@angular/core';
import { parseISO } from 'date-fns';

@Pipe({
  name: 'parseDate',
  standalone: true
})
export class ParseDatePipe implements PipeTransform {

  transform(value: string | null): Date | null {
    if(!value) return null;
    return parseISO(value);
  }

}

import { Pipe, PipeTransform } from '@angular/core';
import { environment } from 'src/environments/environment';

@Pipe({
  name: 'pathFilesServer',
  standalone: true
})
export class PathFilesServerPipe implements PipeTransform {

  transform(value: unknown): string {
    if(value) {
      return environment.URL_FILES_SERVER + value;
    } else {
      return 'assets/images/no-image.webp';
    }
  }

}

import { Pipe, PipeTransform } from '@angular/core';
import { environment } from 'src/environments/environment';

@Pipe({
    name: 'publicPathServer',
    standalone: true
})
export class PublicPathServerPipe implements PipeTransform {

  transform(value: string): string {
    if(value.startsWith('/')) return environment.API_URL + value;
    else return environment.API_URL + '/' + value;
  }

}

