  cssClass?: ((item: T) => string) | string;

  /**
   * @description
   * Agrega clases css al elemento del valor adicional de la celda de la columna
   * @example
   * ...
   * cssClassDisplayAdditionalValue: (item) => item.status === 'active' ? 'text-green-500' : 'text-red-500'
   * ...
