  updateListEvent?: EventEmitter<void>;

  defaultOrder?: {
    key: string,
    order: 'ASC' | 'DESC'
  };

  backButton?: boolean;

  updateButton?: boolean;

  server: {
    readonly url: string,
    queryParams?: { [key: string]: any } | string;
  }

