   * displayValueFn: (item) => item.price, // output: 2,000.00
   * ...
   * type: 'date',
   * displayValueFn: (item) => item.created_at, // output 12/12/2020
   * ...
   */
  type?: TypeValueKeyItem | 'image' | 'html';

  image?: {
    prefixUrl?: string;
    width?: number;
    height?: number;
    alt?: string;
  }
}

