interface ImageListColumn<T = any> extends SimpleListColumn2<T> {
  displayValueFn: (item: T, index: number) => string | null | undefined;
  image?: {
    prefixUrl?: string;
    width?: number;
    height?: number;
    alt?: string;
  }
}

interface HtmlListColumn<T = any> extends SimpleListColumn2<T> {
  displayValueFn: (item: T, index: number) => string | null | undefined;
}

