enum Recurrent {
    Hourly = 'hourly',
    Daily = 'daily',
    Weekly = 'weekly',
    Monthly = 'monthly',
    Yearly = 'yearly',
}

interface StorageConfiguration<T = any> {
    data: any;
    recurrent: Recurrent;
    lastUpdate: Date;
}

interface ModuleConfiguration {
