import { inject, Injectable } from '@angular/core';
import { StorageMap } from '@ngx-pwa/local-storage';
import { FetchService } from './fetch.service';
import { PaginatorData } from 'src/app/shared/interfaces/paginator';
import { getWeek } from 'date-fns';

const SAL_LONG = 16;
const INITIALIZATION_VECTOR_LONG = SAL_LONG;

const bufferABase64 = (buffer: any) => btoa(String.fromCharCode(...new Uint8Array(buffer)));
const base64ABuffer = (buffer: any) => Uint8Array.from(atob(buffer), c => c.charCodeAt(0));

const passwordBasedKeyDerivation = async (password: string, sal: any, iterations: number, long: number, hash: any, algoritmo = 'AES-CBC') => {
    const encoder = new TextEncoder();
    let key = await window.crypto.subtle.importKey('raw', encoder.encode(password), { name: 'PBKDF2' }, false, ['deriveKey']);
    return await window.crypto.subtle.deriveKey(
        {
            name: 'PBKDF2',
            salt: encoder.encode(sal),
            iterations,
            hash
        },
        key,
        { name: algoritmo, length: long },
        false,
        ['encrypt', 'decrypt'],
    );
}

const encrypt = async (password: string, textoPlano: any) => {
    const encoder = new TextEncoder();
    const sal = window.crypto.getRandomValues(new Uint8Array(SAL_LONG));
    const initializationVector = window.crypto.getRandomValues(new Uint8Array(INITIALIZATION_VECTOR_LONG));
    const textPlainBuffer = encoder.encode(textoPlano);
    const clave = await passwordBasedKeyDerivation(password, sal, 100000, 256, 'SHA-256');
    const encrypted = await window.crypto.subtle.encrypt({ name: "AES-CBC", iv: initializationVector }, clave, textPlainBuffer);
    return bufferABase64([...sal, ...initializationVector, ...new Uint8Array(encrypted)]);
};

const decrypt = async (password: string, encriptadoEnBase64: any) => {
    const decoder = new TextDecoder();
    const encryptData = base64ABuffer(encriptadoEnBase64);
    const sal = encryptData.slice(0, SAL_LONG);
    const initializationVector = encryptData.slice(0 + SAL_LONG, SAL_LONG + INITIALIZATION_VECTOR_LONG);
    const clave = await passwordBasedKeyDerivation(password, sal, 100000, 256, 'SHA-256');
    const decryptDataBuffer = await window.crypto.subtle.decrypt({ name: "AES-CBC", iv: initializationVector }, clave, encryptData.slice(SAL_LONG + INITIALIZATION_VECTOR_LONG));
    return decoder.decode(decryptDataBuffer);
}

export enum NameModuleDatabase {
    BoxOpenings = 'boxOpenings',
    Banks = 'banks',
    Users = 'users',
    Taxes = 'taxes',
    DocumentTypes = 'documentTypes',
    Plans = 'plans',
    VehicleTypes = 'vehicleTypes',
}

enum Recurrent {
    Hourly = 'hourly',
    Daily = 'daily',
    Weekly = 'weekly',
    Monthly = 'monthly',
    Yearly = 'yearly',
}

interface StorageConfiguration<T = any> {
    data: any;
    recurrent: Recurrent;
    lastUpdate: Date;
}

interface ModuleConfiguration {
    readonly key: NameModuleDatabase;
    readonly recurrent: Recurrent;
    readonly name: string;
    readonly config: {
        url: string;
        queryParams?: string;
    };
    lastUpdate?: Date;
}

@Injectable({ providedIn: 'root' })
export class DatabaseStorageService {

    private storageMap = inject(StorageMap);
    private fetch = inject(FetchService);
    public readonly modules: ModuleConfiguration[] = [
        {
            name: 'Aperturas de caja',
            key: NameModuleDatabase.BoxOpenings,
            recurrent: Recurrent.Hourly,
            config: {
                url: 'box-opening',
                queryParams: 'relations=box.account.bank&box_status=abierto&limit=100'
            },
        },
        {
            name: 'Bancos',
            key: NameModuleDatabase.Banks,
            recurrent: Recurrent.Daily,
            config: {
                url: 'database-storage/banks'
            },
        },
        {
            name: 'Usuarios',
            key: NameModuleDatabase.Users,
            recurrent: Recurrent.Hourly,
            config: {
                url: 'database-storage/users'
            },
        },
        {
            name: 'Valores de impuestos',
            key: NameModuleDatabase.Taxes,
            recurrent: Recurrent.Hourly,
            config: {
                url: 'database-storage/taxes'
            },
        },
        {
            name: 'Tipos de documento',
            key: NameModuleDatabase.DocumentTypes,
            recurrent: Recurrent.Daily,
            config: {
                url: 'database-storage/document-types'
            },
        },
        {
            name: 'Planes',
            key: NameModuleDatabase.Plans,
            recurrent: Recurrent.Daily,
            config: {
                url: 'database-storage/plans'
            },
        },
        {
            name: 'Tipos de vehÃ­culo',
            key: NameModuleDatabase.VehicleTypes,
            recurrent: Recurrent.Daily,
            config: {
                url: 'database-storage/vehicle-types'
            },
        },
    ];

    constructor() {
        this.modules.forEach(module => {
            this.storageMap.get(module.key).subscribe((response) => {
                if(response) module.lastUpdate = (response as StorageConfiguration).lastUpdate;
            })
        });
    }

    public async getOne<T = any>(key: NameModuleDatabase, id: number): Promise<T | undefined> {
        return (await this.getData<any>(key)).find((item) => item.id == id);
    }

    public async getData<T>(key: NameModuleDatabase): Promise<T[]> {
        const module = this.modules.find((e) => e.key == key)!;
        return new Promise(async (resolve) => {
            this.storageMap.get(key).subscribe(async (response) => {
                if (response) {
                    const { recurrent, lastUpdate } = response as StorageConfiguration<T>;

                    const c_year = new Date().getFullYear();
                    const c_month = new Date().getMonth();
                    const c_date = new Date().getDate();
                    const c_week = getWeek(new Date(), { weekStartsOn: 1 });
                    const c_hour = new Date().getHours();
                    
                    const u_year = lastUpdate.getFullYear();
                    const u_month = lastUpdate.getMonth();
                    const u_date = lastUpdate.getDate();
                    const u_week = getWeek(lastUpdate, { weekStartsOn: 1 });
                    const u_hour = lastUpdate.getHours();

                    if (recurrent == Recurrent.Hourly && (c_year != u_year || c_month != u_month || c_date != u_date || c_hour != u_hour)) resolve(await this.getDataServer(module));
                    else if (recurrent == Recurrent.Daily && (c_year != u_year || c_month != u_month || c_date != u_date)) resolve(await this.getDataServer(module));
                    else if (recurrent == Recurrent.Weekly && (c_year != u_year || c_week != u_week)) resolve(await this.getDataServer(module));
                    else if (recurrent == Recurrent.Monthly && (c_year != u_year || c_month != u_month)) resolve(await this.getDataServer(module));
                    else if (recurrent == Recurrent.Yearly && c_year != u_year) resolve(await this.getDataServer(module));

                    const decode = await decrypt('0123456', (response as StorageConfiguration<T>).data);
                    resolve(JSON.parse(decode));
                } else {
                    resolve(this.getDataServer(module));
                }
            });
        });
    }

    public updateDataLocal(key: NameModuleDatabase): void {
        const config = this.modules.find((e) => e.key == key);
        if (!config) return;
        this.getDataServer(config);
    }

    private async getDataServer<T>(module: ModuleConfiguration): Promise<T[]> {
        const { config, key, recurrent } = module;
        const url = `${config.url}?${config.queryParams ? config.queryParams : ''}`;
        const result = await this.fetch.get<T[] | PaginatorData<T>>(url);
        const data = result instanceof Array ? result : result.data;
        module.lastUpdate = new Date();
        const storageConfig: StorageConfiguration<T> = {
            data: await encrypt('0123456', JSON.stringify(data)),
            recurrent,
            lastUpdate: module.lastUpdate,
        }
        this.storageMap.set(key, storageConfig).subscribe(() => { });
        return data;
    }

}

import { Injectable } from '@angular/core';
import { Observable, Subject, filter } from 'rxjs';

interface IEvent<T = any> {
    name: string;
    data: T;
}

@Injectable({
    providedIn: 'root'
})
export class EventsService {

    private event$: Subject<IEvent> = new Subject<IEvent>();

    constructor() { }

    public eventsFiltered<T = any>(nameEvents: string[]): Observable<IEvent<T>> {
        return this.event$.asObservable().pipe(filter(event => nameEvents.some((name) => name == event.name)));
    }

    get getEventObservable(): Observable<IEvent> {
        return this.event$.asObservable();
    }

    public emitEvent<T = any>(nameEvent: string, data: T): void {
        return this.event$.next({ name: nameEvent, data });
    }
}

import { Injectable, inject } from '@angular/core';
import { FetchErrorResponse, FetchErrorType, HTTP_ERROR_CODES, NAME_TOKEN, RequestInitFetch as RequestInit, defaultMessageDialogCreateItem, defaultMessageDialogDeleteItem, defaultMessageDialogUpdateItem } from 'src/app/shared/interfaces/fetch';
import { FetchBase } from '@utility/fetchBase';
import { MatDialog } from '@angular/material/dialog';
import { ToastService } from './toast.service';
import { Router } from '@angular/router';
import { AuthService } from './auth.service';
import { ToastDataPromise } from '@interface/toast';
import { MatSnackBarRef } from '@angular/material/snack-bar';
import { ConfirmDialogData, ConfirmDialogTemplateComponent } from '@component/confirm-dialog-template/confirm-dialog-template.component';
import { environment } from 'src/environments/environment';

export interface ToastForFetch extends Omit<ToastDataPromise, 'promise' | 'success' | 'error'> {
  success: string | ((data: any, snackbarRef: MatSnackBarRef<any>, currentMessage: string) => Promise<string> | string);
  error: string | ((error: any, snackbarRef: MatSnackBarRef<any>, currentMessage: string) => Promise<string> | string);
}

export interface RequestInitFetch extends Omit<RequestInit, 'confirmDialog'> {
  confirmDialog?: ConfirmDialogData | false;
  toast?: ToastForFetch | false;
}


@Injectable({
  providedIn: 'root',
})
export class FetchService extends FetchBase {
  private dialog = inject(MatDialog);
  private toastService = inject(ToastService);
  private router = inject(Router);
  private authService = inject(AuthService);

  private apiUrl: string = environment.API_URL;

  constructor() {
    super(
      {
        interceptHeadersFn: (request) => this.interceptHeadersFn(request),
        interceptErrorsFn: (error, request) => this.interceptErrorsFn(error, request),
      }
    )
  }

  /* -------------------------------------------------------------------------------------- */
  /* -----------------------------------    INTERCEPTORS   -------------------------------- */
  /* -------------------------------------------------------------------------------------- */
  private interceptHeadersFn(requestInit: RequestInitFetch): { [Key: string]: string } {
    const headers = requestInit?.headers || {};
    const token = localStorage.getItem(NAME_TOKEN);
    if (token && !requestInit?.ignoreAuthorization) headers['Authorization'] = `Bearer ${token}`;
    return headers;
  }

  private async interceptErrorsFn(error: FetchErrorResponse, request: RequestInitFetch): Promise<void> {    
    if (request.ignoreInterceptErrors) return; // if ignore intercept errors

    const errorName = error.name;

    if (errorName == FetchErrorType.ABORT) return;

    if (errorName == FetchErrorType.HTTP && error.status == 401 && error.statusText?.includes('Unauthorized')) {
      this.toastService.open('Su sesiÃ³n ha expirado');
      this.authService.removeToken();
      this.router.navigate(['/login']);
      return;
    }

    this.toastService.open(this.getNameError(error) ?? 'Error desconocido');
  }

  private getNameError(error: FetchErrorResponse): string | undefined {
    const errorHttp = HTTP_ERROR_CODES.find((httpError) => httpError.status == error.status);
    return errorHttp?.description;
  }

  /* -------------------------------------------------------------------------------------- */
  /* ------------------------------------   METHODS    ------------------------------------ */
  /* -------------------------------------------------------------------------------------- */

  public async get<T>(url: string, requestInit?: RequestInitFetch, baseUrl: string = this.apiUrl): Promise<T> {
    const { toast, confirmDialog, ...request} = { ...requestInit ?? {} };

    if (confirmDialog) await this.confirmDialog(confirmDialog);

    if (toast) {
      request.ignoreInterceptErrors = true;
      const promise = this._get(`${baseUrl}/${url}`, request);
      return this.resolveToastPromise<T>(promise, toast);
    }

    return this._get(`${baseUrl}/${url}`, request);
  }

  public async post<T>(url: string, body: any, requestInit?: RequestInitFetch, baseUrl: string = this.apiUrl): Promise<T> {
    const { toast, confirmDialog, ...request} = { ...requestInit ?? {} };

    if (confirmDialog != false) await this.confirmDialog(confirmDialog ?? defaultMessageDialogCreateItem());

    if (toast != false) {
      request.ignoreInterceptErrors = true;
      const promise = this._post(`${baseUrl}/${url}`, body, request);
      const toastDataPromise = toast ? toast : {
        loading: 'Guardando registro...',
        success: 'Registro guardado',
        error: (error: any) => `Error al guardar registro: ${error.error?.message ?? 'Error desconocido'}`,
      };
      return this.resolveToastPromise<T>(promise, toastDataPromise, requestInit);
    }

    return this._post(`${baseUrl}/${url}`, body, request);
  }

  public async put<T>(url: string, body: any, requestInit?: RequestInitFetch, baseUrl: string = this.apiUrl): Promise<T> {
    const { toast, confirmDialog, ...request} = { ...requestInit ?? {} };

    if (confirmDialog != false) await this.confirmDialog(confirmDialog ?? defaultMessageDialogUpdateItem());

    if (toast != false) {
      request.ignoreInterceptErrors = true;
      const promise = this._put(`${baseUrl}/${url}`, body, request);
      const toastDataPromise = toast ? toast : {
        loading: 'Actualizando registro...',
        success: 'Registro actualizado',
        error: (error: any) => `Error al actualizar registro: ${error.error?.message ?? 'Error desconocido'}`,
      };
      return this.resolveToastPromise<T>(promise, toastDataPromise, requestInit);
    }

    return this._put(`${baseUrl}/${url}`, body, request);
  }

  public async patch<T>(url: string, body: any, requestInit?: RequestInitFetch, baseUrl: string = this.apiUrl): Promise<T> {
    const { toast, confirmDialog, ...request} = { ...requestInit ?? {} };

    if (confirmDialog != false) await this.confirmDialog(confirmDialog ?? defaultMessageDialogUpdateItem());
    
    if (toast != false) {
      request.ignoreInterceptErrors = true;
      const promise = this._patch(`${baseUrl}/${url}`, body, request);
      const toastDataPromise = toast ? toast : {
        loading: 'Actualizando registro...',
        success: 'Registro actualizado',
        error: 'Error al actualizar registro',
      };
      return this.resolveToastPromise<T>(promise, toastDataPromise, requestInit);
    }

    return this._patch(`${baseUrl}/${url}`, body, request);
  }

  public async delete<T>(url: string, requestInit?: RequestInitFetch, baseUrl: string = this.apiUrl): Promise<T> {
    const { toast, confirmDialog, ...request} = { ...requestInit ?? {} };

    if (confirmDialog != false) await this.confirmDialog(confirmDialog ?? defaultMessageDialogDeleteItem());

    if (toast != false) {
      request.ignoreInterceptErrors = true;
      const promise = this._delete(`${baseUrl}/${url}`, request);
      const toastDataPromise = toast ? toast : {
        loading: 'Eliminando registro...',
        success: 'Registro eliminado',
        error: 'Error al eliminar registro',
      };
      return this.resolveToastPromise<T>(promise, toastDataPromise, requestInit);
    }

    return this._delete(`${baseUrl}/${url}`, request);
  }

  public async blob(url: string, requestInit?: RequestInitFetch, baseUrl: string = this.apiUrl): Promise<Blob> {
    const { toast, confirmDialog, ...request} = { ...requestInit ?? {} };

    if (confirmDialog) await this.confirmDialog(confirmDialog);

    if (toast) {
      request.ignoreInterceptErrors = true;
      const promise = this._blob<Blob>(`${baseUrl}/${url}`, request);
      return this.resolveToastPromise<Blob>(promise, toast, requestInit);
    }

    return this._blob<Blob>(`${baseUrl}/${url}`, request);
  }

  /* -------------------------------------------------------------------------------------- */
  /* ------------------------------------   ACTIONS   ------------------------------------- */
  /* -------------------------------------------------------------------------------------- */
  private confirmDialog(data: ConfirmDialogData): Promise<boolean> {
    return new Promise((resolve, reject) => {
      this.dialog
        .open(ConfirmDialogTemplateComponent, { data })
        .afterClosed()
        .subscribe((res: boolean) => {
          if (res) resolve(true);
          reject({ name: FetchErrorType.CONFIRMATION_DIALOG, message: 'Abort confirmation dialog' });
        });
    });
  }

  private resolveToastPromise<T>(promise: Promise<T>, toastDataPromise: ToastForFetch, requestInit?: RequestInitFetch): Promise<T> {
    return new Promise(async (resolve, reject) => {
      this.toastService.promise(promise, {
        loading: toastDataPromise.loading ?? 'Cargando...',
        success: async (data, snackBarRef, currentMessage) => {
          resolve(data);
          return toastDataPromise.success instanceof Function ? await toastDataPromise.success(data, snackBarRef, currentMessage) : toastDataPromise.success;
        },

        error: (error: FetchErrorResponse, snackBarRef, currentMessage) => {
          if (error.name == FetchErrorType.ABORT) {
            snackBarRef.dismiss();
            reject(error);
            return currentMessage;
          } else if (error.name == FetchErrorType.HTTP && error.status == 401 && error.statusText?.includes('Unauthorized') && !requestInit?.ignoreAuthorization) {
            this.authService.removeToken();
            this.router.navigate(['/login']);
            return 'Su sesiÃ³n ha expirado, vuelva a iniciar';
          }
          reject(error);
          return toastDataPromise.error instanceof Function ? toastDataPromise.error(error, snackBarRef, currentMessage) : toastDataPromise.error;
        },
      });
    });
  }

}

import { Injectable, inject } from '@angular/core';
import { MatSnackBar, MatSnackBarHorizontalPosition, MatSnackBarVerticalPosition } from '@angular/material/snack-bar';
import { ToastDataPromise, ToastDataSimple } from '@interface/toast';
import { SnackbarTemplateComponent } from 'src/app/shared/components/snackbar-template/snackbar-template.component';

@Injectable({
  providedIn: 'root',
})
export class ToastService {

  private snackBar = inject(MatSnackBar);
  private readonly horizontalPosition: MatSnackBarHorizontalPosition = 'start';
  private readonly verticalPosition: MatSnackBarVerticalPosition = 'bottom';

  public open(message: string, data?: Omit<ToastDataSimple, 'message'>) {
    const snackbar = this.snackBar.openFromComponent(SnackbarTemplateComponent, {
      horizontalPosition: this.horizontalPosition,
      verticalPosition: this.verticalPosition,
      duration: (data?.duration ?? 10) * 1000,
      data: { simple: { ...data, message } },
    });

    if (data?.action) snackbar.onAction().subscribe(() => data.action?.fn(data.data ?? null));

    if (data?.autoCloseFn) snackbar.afterDismissed().subscribe(() => data.autoCloseFn?.(data.data ?? null));

  }

  public promise<T = any>(promise: Promise<T>, configuration?: Partial<Omit<ToastDataPromise, 'promise'>>): void {
    const data: ToastDataPromise = {
      promise,
      loading: configuration?.loading ?? 'Cargando...',
      success: configuration?.success ?? 'Completado',
      error: configuration?.error ?? 'Error al realizar la operaciÃ³n',
    }

    this.snackBar.openFromComponent(SnackbarTemplateComponent, {
      horizontalPosition: this.horizontalPosition,
      verticalPosition: this.verticalPosition,
      duration: 60 * 1000,
      data: { promise: data }
    });
  }

}

