/* --------------------------------------------------------------------- */
/* ----------------------     APPS  MENU     --------------------------- */
/* --------------------------------------------------------------------- */
.settings-menu {
    border-radius: var(--corner-mdc-dialog, 28px) !important;
}

.settings__menu__container {
    min-width: 280px;
    max-width: 280px;
    min-height: 200px;
    overflow: hidden;
    overflow-y: auto;
    padding: 12px 0;
}
.settings__menu__container > .settings__menu__subtitle:first-of-type {
    border-top: none;
    padding-top: 0;
    margin-top: 0;
}

.settings__menu__subtitle {
    font-family: var(--label-medium-font-family-name);
    font-style: var(--label-medium-font-family-style);
    font-weight: var(--label-medium-font-weight);
    font-size: var(--label-medium-font-size);
    line-height: var(--label-medium-line-height);
    letter-spacing: var(--label-medium-letter-spacing);
    color: var(--color-on-surface-variant);
    padding: 12px 16px 0px 16px;
    margin: 12px 0;
    border-top: 1px solid var(--color-outline-variant);
}

.settings__menu__item {
    display: flex;
    justify-content: space-between;
    gap: 10px;
    padding: 8px 16px;
    font-family: var(--body-medium-font-family-name);
    font-style: var(--body-medium-font-family-style);
    font-weight: var(--body-medium-font-weight);
    font-size: var(--body-medium-font-size);
    line-height: var(--body-medium-line-height);
    letter-spacing: var(--body-medium-letter-spacing);
    color: var(--color-on-surface-variant);
}

.settings__menu__item__items-center {
    align-items: center;
}

.settings__menu__item__options__themes {
    display: flex;
    justify-content: flex-end;
    flex-wrap: wrap;
    gap: 4px;
}

.settings__menu__item__options__themes__item {
    width: 32px;
    height: 32px;
    border-radius: 50%;
    cursor: pointer;
    background: rgb(var(--rgb-primary));
}

.settings__menu__item__options__themes__item.active {
    border: 3px solid rgb(var(--rgb-surface));
}

