import { DatePipe } from '@angular/common';
import { Component, EventEmitter, Input, OnInit, Output, SimpleChanges } from '@angular/core';
import { FormControl, FormGroup, ReactiveFormsModule, Validators } from '@angular/forms';
import { MatFormFieldModule } from '@angular/material/form-field';
import { Taxe } from '@interface/taxe';

@Component({
  selector: 'app-taxes-list-card',
  templateUrl: './taxes-list-card.component.html',
  standalone: true,
  imports: [MatFormFieldModule, ReactiveFormsModule, DatePipe],
  styleUrls: ['./taxes-list-card.component.scss']
})
export class TaxesListCardComponent implements OnInit {

  @Input() item!: Taxe;
  @Output() submitForm : EventEmitter<Taxe> = new EventEmitter();

  public form : FormGroup = new FormGroup ({
    value : new FormControl('', Validators.required),
    description : new FormControl('')
  })

  constructor() { }

  ngOnInit(): void {
    this.form.patchValue(this.item)
    this.form.disable();
  }

  ngOnChanges(changes: SimpleChanges){
    this.form.patchValue(this.item)
    this.form.disable()

  }

  public cancelEdit(){
    this.form.patchValue(this.item)
    this.form.disable();
  }

  public _submitForm(){
    if(this.form.valid){
      this.submitForm.emit(this.form.value)
    }
  }
}

import { Component, OnInit, inject } from '@angular/core';
import { getTaxe } from '@helper/index';
import { Taxe } from '@interface/taxe';
import { FetchService } from '@service/fetch.service';
import { TaxesListCardComponent } from '../taxes-list-card/taxes-list-card.component';

@Component({
    selector: 'app-taxes-list',
    templateUrl: './taxes-list.component.html',
    standalone: true,
    imports: [TaxesListCardComponent],
    styleUrls: ['./taxes-list.component.scss'],
})
export class TaxesListComponent implements OnInit {

    public data: Taxe[] = [];
    public total_data: number = 0;
    public loading: boolean = false;
    private fetch = inject(FetchService);

    ngOnInit(): void {
        this.getData();
    }


    private async getData() {
        this.data = await this.fetch.get('taxe');

    }

    public async updateItem(index: number, data: Taxe) {
        const response: any = await this.fetch.put('taxe/' + this.data[index].id, data);
        if (!response) return;
        this.data[index] = response;

    }
}

