(queryParams: { [key: string]: any }, ignoreKeys: string[] = []): URLSearchParams => {
    const query_params = JSON.parse(JSON.stringify(queryParams));
    var searchParams = new URLSearchParams();
    for (const key in query_params) {
        if (query_params[key] === '' || query_params[key] === null || query_params[key] === undefined  || query_params[key] === false || ignoreKeys.includes(query_params[key]))
            continue;
        searchParams.append(key, query_params[key]);
    }
    return searchParams;
}

async (password: string, sal: any, iterations: number, long: number, hash: any, algoritmo = 'AES-CBC') => {
    const encoder = new TextEncoder();
    let key = await window.crypto.subtle.importKey('raw', encoder.encode(password), { name: 'PBKDF2' }, false, ['deriveKey']);
    return await window.crypto.subtle.deriveKey(
        {
            name: 'PBKDF2',
            salt: encoder.encode(sal),
            iterations,
            hash
        },
        key,
        { name: algoritmo, length: long },
        false,
        ['encrypt', 'decrypt'],
    );
}

