  else return 'drawer-dark-full';
}

() => {
  if (
    localStorage.getItem('apareanceInputs') == 'fill' ||
    localStorage.getItem('apareanceInputs') == 'outline'
  )
    return localStorage.getItem(
      'apareanceInputs'
    ) as MatFormFieldAppearance;
  else return 'fill';
}

() => {
  if (
    localStorage.getItem('themeApp') == 'dark' ||
    localStorage.getItem('themeApp') == 'light'
  )
    return localStorage.getItem('themeApp') as 'dark' | 'light';
  else if (window.matchMedia('(prefers-color-scheme: dark)').matches)
    return 'system-dark';
  else return 'system-light';
}

(nameApareance: ThemeColorName) => {
  localStorage.setItem('apareanceApp', nameApareance);
  const theme = getApareanceApp();
  for (const e of THEME_COLOR_NAMES) {
    document.body.classList.remove('theme-color-' + e);
  }
  document.body.classList.add('theme-color-' +theme);
}

(nameApareance: TypeDrawerApp) => {
  localStorage.setItem('apareanceDrawer', nameApareance);
  const theme = getApareanceDrawer();
  document.body.classList.remove('drawer-standard');
  document.body.classList.remove('drawer-dark-full');
  document.body.classList.add(theme);
}

() => {
  const theme = getThemeApp();
  setThemeApp(theme);
  const appearance = getApareanceApp();
  setApareanceApp(appearance);
}

(nameTheme: TypeTheme) => {
  localStorage.setItem('themeApp', nameTheme);
  const theme = getThemeApp();
  if (theme == 'dark' || theme == 'system-dark') {
    document.body.classList.add('dark');
    document.body.classList.remove('light');
  } else {
    document.body.classList.remove('dark');
    document.body.classList.add('light');
  }
}

src/app/shared/helpers/index.ts.defaultValue:
async (fetch: FetchService, documentNumber: string, abortController: AbortController = new AbortController()): Promise<DataPerson | undefined> => {
    let type = '';
    if (documentNumber.length == 8) type = 'dni';
    else if (documentNumber.length == 11) type = 'ruc';
    else return undefined;

    const data = await fetch.get<any>(`${type}/${documentNumber}`, {
        signal: abortController.signal,
        toast: {
            loading: `Buscando ${type.toUpperCase()}...`,
            success: `${type.toUpperCase()} encontrado`,
            error: `No se encontrÃ³ el ${type.toUpperCase()}`,
        }
    });
    if ((data as DataDNI).nombres) return {
        document_type: 'DNI',
        document_number: documentNumber,
        full_name: `${data.nombres} ${data.apellidoPaterno} ${data.apellidoMaterno}`,
        name: data.nombres,
        mother_last_name: data.apellidoMaterno,
        father_last_name: data.apellidoPaterno,
        verifier_code: data.codVerifica,
    }
    else if ((data as DataRUC).nombre_o_razon_social) return {
        document_type: 'RUC',
        document_number: documentNumber,
        full_name: data.nombre_o_razon_social,
        business_name: data.nombre_o_razon_social,
        address: data.direccion,
        full_address: data.direccion_completa,
        retaining_agent: data.es_agente_de_retencion,
        sunat_condition: data.estado,
        legal_representatives: data.representantes_legales ?? [],
    }
    else return undefined;
}

